import React, { Component } from "react";

class MyClock extends Component {
  state = { date: new Date() };
  tick = () => {
    return this.setState(new Date().toLocaleTimeString());
  };
  render() {
    return (
      <div>
        <h1>My Clock</h1>
        <h2>It is: {tick}</h2>
      </div>
    );
  }
}

export default MyClock;
